"
CREATE TABLE archive (id INTEGER PRIMARY KEY AUTOINCREMENT, full_path TEXT, name TEXT, nb_files INTEGER, size INTEGER, last_position INTEGER, date_added DATE, accessed INTEGER, UNIQUE(full_path), UNIQUE(name));
CREATE TABLE tag (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT, UNIQUE(name));
CREATE TABLE tag_archive (id INTEGER PRIMARY KEY AUTOINCREMENT, tag_id INTEGER, archive_id INTEGER, archive_position INTEGER, UNIQUE(tag_id, archive_id, archive_position));
CREATE TRIGGER insert_archive_date_added AFTER INSERT ON archive BEGIN UPDATE archive SET date_added = DATETIME('NOW') WHERE rowid = new.rowid; END;
INSERT INTO tag (name) VALUES ('Star');
"
